<button on:click={login} {disabled} class="facebook-auth" style="--button-width: {buttonWidth}; --button-disabled-color: {disabledButtonBackgroundColor}; --button-color : {buttonBackgroundColor}">
  <img src="data:image/png;base64," alt="Facebook" />
  <span style='--text-color: {buttonTextColor}'>{text}</span>
</button>

<style>
  button {
    width: var(--button-width);
    border: 0;
    background-color: var(--button-color);
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 2px;
    padding: 5px 1px;
    cursor: pointer;
  }

  button:disabled {
    background-color: var(--button-disabled-color);
  }

  img {
    padding: 4px;
    height: 36px;
    width: 36px;
    border-radius: 2px;
    margin: 0;
  }

  span {
    font-family: Roboto, sans-serif;
    font-size: 14px;
    font-weight: bold;
    padding: 0 12px;
    color: var(--text-color);
  }
</style>

<script>
  import { onMount, createEventDispatcher } from 'svelte'
  import loader from '@beyonk/async-script-loader'

  const dispatch = createEventDispatcher()

  const version = 'v3.2'

  let disabled = true
  export let appId
  export let text = 'Sign in with Facebook'
  export let buttonBackgroundColor = '#4285F4';
  export let disabledButtonBackgroundColor = 'grey';
  export let buttonTextColor = '#ffffff';
  export let buttonWidth = '100%';

  onMount(() => {
    loader(
      '//connect.facebook.net/en_GB/sdk.js',
      () => window['FB'],
      () => initialise()
    )
  })

  function initialise () {
    const FB = window['FB']
    FB.init({
      appId      : appId,
      cookie     : true,
      xfbml      : false,
      version    : version
    })
    disabled = false
  }

  function login () {
    const FB = window['FB']
    FB.login(function (response) {
      if (response.status === 'connected') {
        const authResponse = response.authResponse
        const userId = authResponse.userID
        const accessToken = authResponse.accessToken

        dispatch('auth-success', {
          accessToken,
          userId
        })
      } else {
        dispatch('auth-info', { response })
      }
    }, { scope: 'email,public_profile' })
  }
</script>
