<button on:click={login} {disabled}>
  <img src="data:image/png;base64," alt="Google" />
  <span>{text}</span>
</button>

<style>
  .facebook-auth {
      button {
      width: 100%;
      border: 0;
      background-color: #4285F4;
      display: flex;
      justify-content: center;
      align-items: center;
      border-radius: 2px;
      padding: 5px 1px;
      cursor: pointer;
    }

    button:disabled {
      background-color: grey;
    }

    img {
      padding: 4px;
      height: 36px;
      width: 36px;
      border-radius: 2px;
      margin: 0;
    }

    span {
      font-family: Roboto, sans-serif;
      font-size: 14px;
      font-weight: bold;
      padding: 0 12px;
      color: white;
    }
  }
</style>

<script>
  import { onMount, createEventDispatcher } from 'svelte'
  import loader from '@beyonk/async-script-loader'

  const dispatch = createEventDispatcher()

  const version = 'v3.2'

  let disabled = true
  export let appId
  export let text = 'Sign in with Facebook'

  onMount(() => {
    loader(
      '//connect.facebook.net/en_GB/sdk.js',
      () => window['FB'],
      () => initialise()
    )
  })

  function initialise () {
    const FB = window['FB']
    FB.init({
      appId      : appId,
      cookie     : true,
      xfbml      : false,
      version    : version
    })
    disabled = false
  }

  function login () {
    const FB = window['FB']
    FB.login(function (response) {
      if (response.status === 'connected') {
        const authResponse = response.authResponse
        const userId = authResponse.userID
        const accessToken = authResponse.accessToken

        dispatch('auth-success', {
          accessToken,
          userId
        })
      } else {
        dispatch('auth-info', { response })
      }
    }, { scope: 'email,public_profile' })
  }
</script>
